import React from 'react';
import './App.css';
import { Route, Switch,Redirect } from 'react-router-dom';
import Footer from './components/Footer/Footer';
import HomePage from './components/HomePage/Homepage';
import Navbar from './components/Navbar/Navbar';
import Aboutus from './components/AboutUs/Aboutus';
import Contactus from './components/ContactUs/Contactus';
import Login from './components/Login/Login';
import { useState } from 'react';
import { useEffect } from 'react';
import Dashboard from './components/Dashboard/Dashboard';

function App() {
  // DECLARE LOGIN STATE FOR APP
  const [state, setState] = useState({
    logged_in: localStorage.getItem('token') ? true : false,
    email: ''
  });

  useEffect(() => {
    if (state.logged_in) {
      fetch('http://localhost:8000/api/current_user/', {
        headers: {
          Authorization: `JWT ${localStorage.getItem('token')}`
        }
      })
        .then(res => res.json())
        .then(json => {
          console.log(json);
          setState({ email: json.email });
        });
    }
  }, []);

  return (
    <div>
      <Switch>
        <Route exact path ="/">
            <Navbar userState={state} manageState={setState}/>
            <HomePage/>
            <br/>
            <Footer/>
        </Route>
        <Route exact path ="/dashboard">
            <Navbar userState={state} manageState={setState}/>
            <Dashboard userState={state} manageState={setState}/>
            <br/>
            <Footer/>
        </Route>
        <Route exact path ="/aboutus">
            <Navbar userState={state} manageState={setState}/>
            <Aboutus/>
            <br/>
            <Footer/>
        </Route>
        <Route exact path ="/login">
            <Navbar handle_logout={handle_logout}
            userState={state}
            manageState={setState}/>

            <Login handle_login={handle_login} 
            handle_logout={handle_logout}
            handle_signup={handle_signup}
            userState={state}
            manageState={setState}/>
            <br/>
            <Footer/>
        </Route>
        {/* <Route exact path ="/contactus">
            <Navbar/>
            <Contactus/>
            <br/>
            <Footer/>
        </Route> */}
        <Redirect to="/" />
      </Switch>
    </div>
  );
}

export default App;
