import React from 'react';
import './App.css';
import { Route, Switch,Redirect } from 'react-router-dom';
import Footer from './components/Footer/Footer';
import HomePage from './components/HomePage/Homepage';
import Navbar from './components/Navbar/Navbar';
import Aboutus from './components/AboutUs/Aboutus';
import Contactus from './components/ContactUs/Contactus';
import Login from './components/Login/Login';
import { useState } from 'react';
import { useEffect } from 'react';
import Dashboard from './components/Dashboard/Dashboard';

function App() {

  const initialStateFinder = () => {
    let token = localStorage.getItem('token');
    if (token) {
      fetch('http://localhost:8000/api/current_user/', {
        headers: {
          Authorization: `JWT ${localStorage.getItem('token')}`
        }
      })
      .then(res => res.json())
      .then(json => {
          if(json.user === undefined || json.user.email === '' || json.user.email === undefined){
            // If the token is invalid or expired
            return({logged_in: false, email: ''});
          }
          else{
              // If token is correct
            return({logged_in: true, email: json.user.email});
          }
        });
    }
  }

  // DECLARE LOGIN STATE FOR APP
  const [state, setState] = useState({logged_in: false, email: ''});  

  useEffect(() => {
    setState(initialStateFinder());
  }, []);

  return (
    <div>
      <Switch>
        <Route exact path ="/">
            <Navbar userState={state} manageState={setState}/>
            <HomePage/>
            <br/>
            <Footer/>
        </Route>
        <Route exact path ="/dashboard">
            <Navbar userState={state} manageState={setState}/>
            <Dashboard userState={state} manageState={setState}/>
            <br/>
            <Footer/>
        </Route>
        <Route exact path ="/aboutus">
            <Navbar userState={state} manageState={setState}/>
            <Aboutus/>
            <br/>
            <Footer/>
        </Route>
        <Route exact path ="/login">
            <Navbar userState={state} manageState={setState}/>
            <Login userState={state} manageState={setState}/>
            <br/>
            <Footer/>
        </Route>
        <Route exact path ="/contactus">
        <Navbar userState={state} manageState={setState}/>
            <Contactus/>
            <br/>
            <Footer/>
        </Route>
        <Redirect to="/" />
      </Switch>
    </div>
  );
}

export default App;
